import React, { useState } from "react";

const StateRender = () => {
  const [count, setCount] = useState(0);

  return(
    <>
    <p>you Clicked {count} times.</p>
    <button onClick={setCount(count + 1)}> 'add + 1' </button>
    </>
  );  
}

export default StateRender;
