import React, { useState } from 'react'

const allBrands = [
  { id: 1, brand: 'nike' },
  { id: 2, brand: 'puma' },
  { id: 3, brand: 'addida' },
  { id: 4, brand: 'fila' },
  { id: 5, brand: 'jockey' },
]

function AddToCart() {
  const [selectedBrand, setSelectedBrand] = useState([])

  const handleAddToCart = (id) => {
    const Items = allBrands.find((items) => items.id === id)
    setSelectedBrand(Items)
  }

  return (
    <>
      <br /> <br /> <br />
      {allBrands.map((b) => (
        <div>
          <span>{b.brand}</span>
          <button onClick={() => handleAddToCart(b.id)}>{b.brand}</button>
        </div>
      ))}
      {selectedBrand && selectedBrand.map((brand) => brand.brand)}
    </>
  )
}

export default AddToCart
