const Header = () => {
  const incrementHandler = () => {
    console.log(name++)
  }

  return (
    <div className="header" style={{ padding: 10 }}>
      <h1>AR</h1>
      <div className="search-bar" style={{ display: 'flex', gap: '0.5rem' }}>
